mapper.select:
SELECT a.user_id, a.login_name, a.login_pwd, a.user_info_id, a.user_state, b.item_name as user_state_cn, a.user_status, a.user_type, a.user_position, a.user_level, a.user_role, a.add_time, a.update_time, a.lastlogin_time, a.add_user, a.update_user FROM sys_login_user a left join sys_base_dict_item b on a.user_state=b.item_code WHERE 1=1
AND a.login_name < #{parameters.login_name}
ORDER BY user_id
LIMIT #{pageNow}, #{pageSize}

SELECT COUNT(*) FROM sys_login_user
WHERE 1=1


		AND login_name = #{parameters.login_name}

SELECT dict_code as dict_id, dict_code, dict_name, dict_type, dict_remark
FROM sys_base_dict WHERE 1=1
AND dict_code = #{parameters.dict_code}
AND dict_name = #{parameters.dict_name}
AND dict_type = #{parameters.dict_type}
ORDER BY dict_code
LIMIT #{pageNow}, #{pageSize}

SELECT COUNT(*) FROM sys_base_dict
WHERE 1=1


		AND dict_code = #{parameters.dict_code}




		AND dict_name = #{parameters.dict_name}




		AND dict_type = #{parameters.dict_type}

SELECT
	item_code as item_id,
	item_code,
	item_code,
	item_name,
	dict_code,
	code,
	isused

FROM
	sys_base_dict_item
	WHERE 1=1


		AND item_code = #{parameters.item_code}




		AND item_name = #{parameters.item_name}




		AND dict_code = #{parameters.dict_code}




		AND code = #{parameters.code}




		AND isused = #{parameters.isused}


ORDER BY dict_code


LIMIT #{pageNow}, #{pageSize}

SELECT COUNT(*) FROM sys_base_dict_item
WHERE 1=1


		AND item_code = #{parameters.item_code}




		AND item_name = #{parameters.item_name}




		AND dict_code = #{parameters.dict_code}




		AND code = #{parameters.code}




		AND isused = #{parameters.isused}
